                    {location.pathname==="/list"&&user.infos.user.isAdmin===true&&
                    <button onClick={()=>{
                        navigate('/admin/addUser')
                    }}>Ajouter</button>}
                    <img src={user.infos.user.photo}/>
                    <button onClick={()=>{
                        dispatch(logoutUser())
                        navigate('/login')
                    }}>DÃ©connexion</button>
                 </div>
             </>
                }

        </div>
    )
}

export default Header;
